import React, { useEffect, useState } from "react";
import "./Login.css";
import axios, { HttpStatusCode } from "axios";
import { useNavigate } from "react-router-dom";

function Login() {
  const navigate = useNavigate();
  const [data, setData] = useState({
    email: "",
    password: "",
    name: "",
    role: "",
    tickets: [],
  });

  const verifyEmailHandlder = (e) => {

    const email = document.getElementById("email").value;

    email.addEventListener("keyup", function (event) {
      if (email.validity.typeMismatch) {
        email.setCustomValidity("I expect an e-mail, darling!");
      } else {
        email.setCustomValidity("");
      }
    });

    const indx = email.lastIndexOf("@");
    console.log(indx);
    let last = email.substring(indx,email.length);
    console.log(last);
    if(last === "nucleusteq.com") return true;
    return false;

  }

  const handleEmailChange = (e) => {
    setData({ ...data, email: e.target.value });
  };

  const handlePasswordChange = (e) => {
    setData({ ...data, password: e.target.value });
  };

  const submitHandler = (e) => {
    e.preventDefault();
    if(!verifyEmailHandlder()){
      alert("Enter Valid Email Address.");
      
    }
    else {
     
      console.log(data);
      axios
        .post(
          "http://localhost:8080/api/login",
          // name:"jbkjh",
          // email: data.email,
          // password: data.password
          data
        )
        .then((response) => {
          setData(response.data);
          // if(response.data === "Login Successfully", HttpStatusCode.Accepted){
          navigate("/home");
          // }
          console.log(response.data);
          // alert(response.data)
        })
        .catch((err) => console.log(err));
    }
   
  };

  return (
    <div className="container">
      <div className="registration_form">
        <div className="heading">
          <h1 id="heading">Login</h1>
        </div>
        <div className="input_fields">
          <form className="content" onSubmit={submitHandler}>
            <div className="input_area">
              <label for="email">Email:</label>
              <input
                type="email"
                id="email"
                name="email"
                placeholder="Enter your email "
                onChange={handleEmailChange}
                required
              />
            </div>

            <div className="input_area">
              <label for="password">Password:</label>
              <input
                type="password"
                id="password"
                name="password"
                placeholder="Enter your password"
                onChange={handlePasswordChange}
                required
              />
            </div>

            <div className="registraction_btn">
              <input type="submit" value="Login"  />
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
