import React, { useState } from "react";
import "./UserRegistration.css";

function UserRegistration() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    if (name.trim() === "") {
      alert("Name cannot be empty");
      return;
    }

    if (email.trim() === "") {
      alert("Email cannot be empty");
      return;
    }

    const emailRegex = /^[a-zA-Z0-9._-]+@nucleusteq\.com$/;
    if (!emailRegex.test(email)) {
      alert("Email must have domain nucleusteq.com");
      return;
    }

    if (!email.endsWith("nucleusteq.com")) {
      alert("Email domain must be nucleusteq.com");
      return;
    }

    if (password.length < 8) {
      alert("Password must be at least 8 characters long.");
      return;
    }

    // If all validations pass, you can proceed with form submission or further processing
    // For now, let's just log the success message
    console.log("Form submitted successfully!");
  };

  return (
    <div className="user_container">
      <div className="user_registration_form">
        <div className="user_heading">
          <p>Create New User</p>
        </div>

        <form className="user_form" onSubmit={handleSubmit}>
          <div className="user_input_area">
            <label htmlFor="name">Name:</label>
            <input
              type="text"
              id="name"
              name="name"
              placeholder="Enter your name"
              value={name}
              onChange={handleNameChange}
              required
            />
          </div>

          <div className="user_input_area">
            <label htmlFor="email">Email:</label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="Enter your email"
              value={email}
              onChange={handleEmailChange}
              required
            />
          </div>

          <div className="user_input_area">
            <label htmlFor="password">Password:</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="Enter your password"
              value={password}
              onChange={handlePasswordChange}
              required
            />
          </div>

          {/* Rest of the form fields... */}

          <div className="user_registration_form_btn">
            <input type="submit" value="Register" />
          </div>
        </form>
      </div>
    </div>
  );
}

export default UserRegistration;
